package com.jsonimport com.alibaba.fastjson.JSONimport com.json.Value.JsonObject/** * 使用语句获取json中的数据 * @param executeLanguage 执行语句 * */static List<Object> execute(String executeLanguage, String json){	if(!executeLanguage.startsWith("root")){		return;	}	//生成数据结构	Value value = Analysis.analysis(json);	//解析执行语句		//验证合法性		//转化为内部执行语句	def root = value.root() instanceof JsonObject ? JSON.parseObject(json) : JSON.parseArray(json);		//执行	Binding binding = new Binding();	binding.setVariable("root", root);	GroovyShell shell = new GroovyShell(binding);	Object obj = shell.evaluate("return " + executeLanguage);	if(List.class.isAssignableFrom(obj.getClass())){		List<Object> list = obj;		return list;	} else {		List<Object> list = new ArrayList<>();		list.add(obj);		return list;	}}